require('dotenv').config();
const alchemyKey = process.env.REACT_APP_ALCHEMY_KEY;
const { createAlchemyWeb3 } = require("@alch/alchemy-web3");
const web3 = createAlchemyWeb3(alchemyKey);

const contractABI = require("../contract-abi.json");
const contractAddress = "0xa6447dde6b0a7f7a04ae67cce13b805081c9f829";

export const helloWorldContract = new web3.eth.Contract(
    contractABI,
    contractAddress
  );

export const loadCurrentMessage = async () => {
    const message = await helloWorldContract.methods.message().call();
    return message;
};

export const connectWallet = async () => {
    if (window.ethereum) {
        try {
          const addressArray = await window.ethereum.request({
            method: "eth_requestAccounts",
          });
          const obj = {
            status: "ğŸ‘†ğŸ½ Write a message in the text-field above.",
            address: addressArray[0],
          };
          return obj;
        } catch (err) {
          return {
            address: "",
            status: "ğŸ˜¥ " + err.message,
          };
        }
      } else {
        return {
          address: "",
          status: (
            <span>
              <p>
                {" "}
                ğŸ¦Š{" "}
                <a target="_blank" href={`https://metamask.io/download.html`}>
                  You must install Metamask, a virtual Ethereum wallet, in your
                  browser.
                </a>
              </p>
            </span>
          ),
        };
      }
};

export const getCurrentWalletConnected = async () => {
    if (window.ethereum) {
        try {
          const addressArray = await window.ethereum.request({
            method: "eth_accounts",
          });
          if (addressArray.length > 0) {
            return {
              address: addressArray[0],
              status: "ğŸ‘†ğŸ½ Write a message in the text-field above.",
            };
          } else {
            return {
              address: "",
              status: "ğŸ¦Š Connect to Metamask using the top right button.",
            };
          }
        } catch (err) {
          return {
            address: "",
            status: "ğŸ˜¥ " + err.message,
          };
        }
      } else {
        return {
          address: "",
          status: (
            <span>
              <p>
                {" "}
                ğŸ¦Š{" "}
                <a target="_blank" href={`https://metamask.io/download.html`}>
                  You must install Metamask, a virtual Ethereum wallet, in your
                  browser.
                </a>
              </p>
            </span>
          ),
        };
      }
};

export const updateMessage = async (address, message) => {
    if (!window.ethereum || address === null) {
        return {
          status:
            "ğŸ’¡ Connect your Metamask wallet to update the message on the blockchain.",
        };
      }
    
    if (message.trim() === "") {
    return {
        status: "âŒ Your message cannot be an empty string.",
    };
    }

    //set up transaction parameters
    const transactionParameters = {
        to: contractAddress, // Required except during contract publications.
        from: address, // must match user's active address.
        data: helloWorldContract.methods.update(message).encodeABI(),
    };

    //sign the transaction
    try {
        const txHash = await window.ethereum.request({
        method: "eth_sendTransaction",
        params: [transactionParameters],
        });
        return {
        status: (
            <span>
            âœ…{" "}
            <a target="_blank" href={`https://goerli.etherscan.io/tx/${txHash}`}>
                View the status of your transaction on Etherscan!
            </a>
            <br />
            â„¹ï¸ Once the transaction is verified by the network, the message will
            be updated automatically.
            </span>
        ),
        };
    } catch (error) {
        return {
        status: "ğŸ˜¥ " + error.message,
        };
    }
};

export const approveAmt = async (my_address, owner_address, amount) => {
    if (!window.ethereum || my_address === null) {
        return {
          status:
            "ğŸ’¡ Connect your Metamask wallet to update the message on the blockchain.",
        };
      }
    
    if (amount == 0) {
    return {
        status: "âŒ Your amount cannot be 0.",
    };
    }

    let _data = null
    
        try{
            console.log('approving...')
            _data = await helloWorldContract.methods.approve(owner_address, amount).send({from: my_address})
            console.log(_data)
            return {
                status: (
                    <span>
                    âœ…{" "}
                    <a target="_blank" href={`https://goerli.etherscan.io/tx/${_data.transactionHash}`}>
                        View the status of your transaction on Etherscan!
                    </a>
                    <br />
                    â„¹ï¸ Once the transaction is verified by the network, the message will
                    be updated automatically.
                    </span>
                ),
                };
        }
        catch (error) {
            return {
                status: "ğŸ˜¥ " + error.message,
            }
        }
};

export const tokenTransfer = async (my_address, receiver_address, amount) => {
    if (!window.ethereum || my_address === null) {
        return {
          status:
            "ğŸ’¡ Connect your Metamask wallet to update the message on the blockchain.",
        };
      }
    
    if (amount === 0) {
    return {
        status: "âŒ Your amount cannot be 0.",
    };
    }

    let _data = null
    
        try{
            console.log('transferring...')
            _data = await helloWorldContract.methods.transfer(receiver_address, amount).send({from: my_address})
            console.log(_data)
            return {
                status: (
                    <span>
                    âœ…{" "}
                    <a target="_blank" href={`https://goerli.etherscan.io/tx/${_data.transactionHash}`}>
                        View the status of your transaction on Etherscan!
                    </a>
                    <br />
                    â„¹ï¸ Once the transaction is verified by the network, the message will
                    be updated automatically.
                    </span>
                ),
                };
        }
        catch (error) {
            return {
                status: "ğŸ˜¥ " + error.message,
            }
        }
}

export const allowanceTransfer = async (my_address, from_address, to_address, amount) => {
    if (!window.ethereum || my_address === null) {
        return {
          status:
            "ğŸ’¡ Connect your Metamask wallet to update the message on the blockchain.",
        };
      }
    
    if (amount === 0) {
    return {
        status: "âŒ Your amount cannot be 0.",
    };
    }

    let _data = null
    
        try{
            console.log('transferring...')
            _data = await helloWorldContract.methods.transferFrom(from_address, to_address, amount).send({from: my_address})
            console.log(_data)
            return {
                status: (
                    <span>
                    âœ…{" "}
                    <a target="_blank" href={`https://goerli.etherscan.io/tx/${_data.transactionHash}`}>
                        View the status of your transaction on Etherscan!
                    </a>
                    <br />
                    â„¹ï¸ Once the transaction is verified by the network, the message will
                    be updated automatically.
                    </span>
                ),
                };
        }
        catch (error) {
            return {
                status: "ğŸ˜¥ " + error.message,
            }
        }
}
